import axios from 'axios';

import UserSelect from '../components/UserSelect';
import {Div50, Divflex100} from '../mystyle/mystyle';

import { useState } from 'react';

const FreeBoard = () => {
    const [email, setEmail] = useState('');
    const [ok,setOk] = useState("");
    const emailinput = e => {
      setEmail(e.target.value);
    };

    const [name, setName] = useState('');
    const nameInput = e => {
      setName(e.target.value);
    };

    const [password, setPassword] = useState('');
    const passwordInput = e => {
      setPassword(e.target.value);
    };

    const dosave = () => {
      axios
        .post(`http://localhost:9000/users/insert`, {
          email,
          name,
          password,
        })
        .then(() => {
          setEmail('');
          setName('');
          setPassword('');
        })
        .catch(e => {
          console.log(e);
        });
    };
  return (
    <Divflex100 style={{height:"300px"}}>
      <Div50>
        <div>
          <h1>insert</h1>
        </div>
        <div>
          <div>
            <label>email</label>
            <input type="text" onChange={emailinput} value={email} />
          </div>
          <div>
            <label>name</label>
            <input type="text" onChange={nameInput} value={name} />
          </div>
          <div>
            <label>password</label>
            <input type="text" onChange={passwordInput} value={password} />
          </div>
          <button onClick={dosave}>insert</button>
        </div>
      </Div50>
      <Div50>
        <UserSelect email={email}></UserSelect>
      </Div50>
    </Divflex100>
  );
};

export default FreeBoard;
