import {useState} from 'react';
import {Link} from 'react-router-dom';

const MyNav = () => {
  const [mystyle, setMystyle] = useState({
    opacity: '1',
    transition: 'opacity 1s ease-in-out',
    display: 'block',

  });
  const menu = () => {
    setMystyle(e => {
      return e.opacity === '1'
        ? {  opacity: '0', transition: 'opacity 1s ease-in-out',display: 'none',}
        : {opacity: '1', transition: 'opacity 1s ease-in-out',display: 'block',};
    });
  };
  return (
    <div>
      <h1 style={{cursor: 'pointer', paddingLeft: '.5rem'}} onClick={menu}>
        메뉴
      </h1>
      <div style={mystyle}>
        <Link to="user">User</Link>
        <Link to="freeboard">FreeBoard</Link>
        <Link to="login">Login</Link>
      </div>
    </div>
  );
};

export default MyNav;
