import { useState } from 'react';
import {Link} from 'react-router-dom';

const MyNav = () => {
  const [mystyle,setMystyle] = useState({ display:"block", transition: "display 1s"});
  const menu = ()=>{
    setMystyle( e=>{
      return e.display ==='block' ? { display:"none"}:{ display:"block"};
    })
  }
  return (
    <div>
      <h1 style={{cursor:"pointer",paddingLeft:".5rem"}} onClick={menu}>메뉴</h1>
      <div style={mystyle}>
        <Link to="user">User</Link>
        <Link to="freeboard">FreeBoard</Link>
        <Link to="login">Login</Link>
      </div>
    </div>
  );
};

export default MyNav;
