import './App.css';
import React, {createContext, useState} from 'react';
import MyNav from './components/MyNav';
import User from './components/User';
import {Route, Routes} from 'react-router-dom';
import FreeBoard from './components/FreeBoard';

export const selectContext = createContext();

function fontSize(){
  return ( <div>
    <h1>전체 글자크기 바꾸기</h1>
    <button style={{width:"50px",height:"50px"}}>+</button>
    <button style={{width:"50px",height:"50px"}}>-</button>
  </div>);
}

function App() {
  const [ok, setOk] = useState('User 추가해주세요');
  return (
    <>
      <MyNav />
      <selectContext.Provider value={{ok, setOk}}>
        <Routes>
          <Route
            path="/"
            element={<h1 style={{padding: '1rem'}}>메뉴를 클릭해주세요</h1>}
          ></Route>
          <Route path="user" element={<User />}></Route>
          <Route path="freeboard" element={<FreeBoard />}></Route>
        </Routes>
      </selectContext.Provider>

    </>
  );
}

export default App;
