    setEmail(e.target.value);
  };

  const [name, setName] = useState('');
  const nameInput = e => {
    setName(e.target.value);
  };

  const [password, setPassword] = useState('');
  const passwordInput = e => {
    setPassword(e.target.value);
  };

  const dosave = () => {
    axios
      .post(`http://localhost:9000/users/insert`, {
        email,
        name,
        password,
      })
